"use strict";(self.webpackChunkdoc_camp=self.webpackChunkdoc_camp||[]).push([[420],{6874:e=>{e.exports=JSON.parse('{"blogPosts":[{"id":"24011","metadata":{"permalink":"/doc_camp/blog/24011","source":"@site/blog/2019-05-28.md","title":"\u3010\u5c71\u77e5\u8b58\u3011\u4e09\u500b\u4fdd\u6696\u91cd\u9ede\uff01\u5e36\u4f60\u627e\u5230\u6700\u9069\u5408\u7684\u7fbd\u7d68","description":"This is my first post on Docusaurus 2.","date":"2021-11-09T20:22:00.000Z","formattedDate":"November 9, 2021","tags":[{"label":"\u767b\u5c71\u77e5\u8b58","permalink":"/doc_camp/blog/tags/\u767b\u5c71\u77e5\u8b58"}],"readingTime":3.26,"hasTruncateMarker":true,"authors":[{"name":"Kent Shen","title":"\u524d\u7aef\u5de5\u7a0b\u5e2b","url":"https://github.com/observer1220","email":"observer1220@gmail.com","imageURL":"https://github.com/observer1220.png","key":"kent"}],"frontMatter":{"slug":"24011","title":"\u3010\u5c71\u77e5\u8b58\u3011\u4e09\u500b\u4fdd\u6696\u91cd\u9ede\uff01\u5e36\u4f60\u627e\u5230\u6700\u9069\u5408\u7684\u7fbd\u7d68","description":"This is my first post on Docusaurus 2.","authors":["kent"],"tags":["\u767b\u5c71\u77e5\u8b58"],"hide_table_of_contents":false,"date":"2021-11-09T20:22"},"nextItem":{"title":"\u3010\u7522\u54c1\u3011Smartwool\u651c\u624b\u7642\u7652\u7cfb\u63d2\u756b\u5bb6\uff01\u7e6a\u51fa\u79cb\u51ac\u65b0\u54c1\u6236\u5916\u63a2\u7d22\u7cbe\u795e","permalink":"/doc_camp/blog/24012"}},"content":"\u4fdd\u6696\u4e43\u662f\u51ac\u5929\u6700\u91cd\u8981\u7684\u4e8b\uff0c\u7121\u8ad6\u767b\u5c71\u3001\u9732\u71df\u3001\u6ed1\u96ea\u3001\u65c5\u904a\u7b49\u6236\u5916\u6d3b\u52d5\uff0c\u66f4\u662f\u9700\u8981\u4e00\u4ef6\u8db3\u5920\u4fdd\u6696\u7684\u7fbd\u7d68\uff0c\u4f86\u7dad\u6301\u81ea\u5df1\u8eab\u9ad4\u7684\u71b1\u80fd\uff0c\u56e0\u6b64\u6311\u9078\u7fbd\u7d68\u7d55\u5c0d\u4e0d\u80fd\u99ac\u4e4e\uff01\u53ea\u4e0d\u904e\u5e02\u9762\u4e0a\u90a3\u9ebc\u591a\u6b3e\u5f0f\u7684\u7fbd\u7d68\uff0c\u5230\u5e95\u54ea\u4e00\u7a2e\u9069\u5408\u81ea\u5df1\uff1f\u5b83\u8db3\u5920\u966a\u6211\u5ea6\u904e\u5c71\u4e0a\u7684\u591c\u665a\u55ce\uff1f\\n\\n\x3c!--truncate--\x3e\\n\\n\u7de8\u8f2f\u6574\u7406\u4e09\u500b\u7fbd\u7d68\u4fdd\u6696\u91cd\u9ede\uff0c\u9858\u5927\u5bb6\u90fd\u80fd\u6311\u9078\u5230\u6700\u9069\u5408\u81ea\u5df1\u7684\u7fbd\u7d68\u3002\\n\\n![image](https://cdntwrunning.biji.co/800_76ae270c33aee47d448c932492e91bfe.jpg)\\n\\n### \u278a Fill Power (FP)\\n\\n\u4e2d\u6587\u7ffb\u8b6f\u6709\uff1a\u84ec\u9b06\u4fc2\u6578\u3001\u81a8\u8139\u4fc2\u6578\u3001\u84ec\u9b06\u5ea6\u3002\\n\\nFill Power \u6108\u9ad8\uff0c\u7559\u5728\u7fbd\u7d68\u9699\u7e2b\u9593\u7684\u7a7a\u6c23\u4e5f\u6703\u6108\u591a\uff0c\u66f4\u80fd\u6709\u6548\u5c07\u4eba\u9ad4\u7522\u751f\u7684\u71b1\u80fd\u8207\u5916\u90e8\u51b7\u7a7a\u6c23\u9694\u7d55\uff0c\u56e0\u6b64\u4ee5\u76f8\u540c\u7fbd\u7d68\u91cd\u91cf\u4f86\u6bd4\u8f03\uff0cFill Power \u6108\u9ad8\uff0c\u4fdd\u6696\u5ea6\u6108\u9ad8\uff0c\u4e5f\u6108\u8f15\u5de7\u3002\\n\\n\u5e02\u9762\u4e0a\u5e38\u898b\u7684\u767b\u5c71\u7fbd\u7d68\u7684 Fill Power\uff0c\u5927\u591a\u5728 600 \u81f3 900\uff0c900+\u7b97\u662f\u76ee\u524d\u6700\u9802\u7d1a\u7684\u7fbd\u7d68\u3002\\n\\n### \u278b \u7fbd\u7d68\u6bd4\u4f8b\\n\\n\u4e00\u4ef6\u7fbd\u7d68\u8863\u542b\uff1a\u7fbd\u7d68(Down)\u3001\u7fbd\u6bdb(Feather)\uff0c\u7fbd\u7d68\u69cb\u9020\u70ba\u7403\u72c0\u7e96\u7dad\u9ad4\uff0c\u80fd\u5f62\u6210\u7a7a\u6c23\u9694\u7d55\u5c64\uff0c\u5c07\u8eab\u9ad4\u7522\u751f\u7684\u71b1\u80fd\u4fdd\u6301\u5728\u5916\u5957\u5167\u90e8\uff1b\u7fbd\u6bdb\u5247\u662f\u5c0f\u7fbd\u6bdb\u6897\uff0c\u5b8c\u5168\u4e0d\u5177\u4fdd\u6696\u6548\u679c\uff0c\u56e0\u6b64\u7fbd\u7d68\u6bd4\u4f8b\u6108\u9ad8\u4e5f\u5c31\u6108\u4fdd\u6696\u3002\\n\\n\u73fe\u4eca\u9084\u6c92\u8fa6\u6cd5\u505a\u5230 100%\u7fbd\u7d68\uff0c\u6703\u6709\u4e00\u5b9a\u6bd4\u4f8b\u7684\u5c0f\u7fbd\u6bdb\u6897\uff0c\u4f8b\u5982 90/10 \u8868\u793a\u542b\u7fbd\u7d68 90%\u7fbd\u6bdb 10%\u3002\\n\\n### \u278c \u7fbd\u7d68\u7a2e\u985e\\n\\n\u7fbd\u7d68\u6709\u5206\u70ba\u300c\u9d5d\u7d68\u300d\u548c\u300c\u9d28\u7d68\u300d\u3002\u76f8\u8f03\u65bc\u9d28\u7d68\uff0c\u9d5d\u7d68\u7684\u7d68\u7d72\u9577\uff0c\u7d68\u6735\u5927\uff0c\u84ec\u9b06\u6027\u597d\uff0c\u56de\u5f48\u5ea6\u597d\uff0c\u54c1\u8cea\u8f03\u9ad8\uff0c\u56e0\u6b64\u50f9\u683c\u4e5f\u6703\u6bd4\u8f03\u9ad8\u3002\\n\\n![image](https://cdntwrunning.biji.co/800_cfc233a7ffb60a3572b7ecc20862f281.jpg)\\n\\n:::tip\\n\u88dc\u5145\u8aaa\u660e\uff1a\u9664\u4e86\u4e0a\u8ff0\u4e09\u500b\u91cd\u9ede\uff0c\u6311\u9078\u7fbd\u7d68\u6642\u4e5f\u8981\u76e1\u91cf\u6311\u9078\u5408\u8eab\u7684\u5c3a\u5bf8\uff0c\u4fdd\u6696\u5ea6\u6703\u6bd4\u8f03\u597d\uff01\\n\\n\u53e6\u5916\uff0c\u7fbd\u7d68\u96d6\u7136\u5177\u512a\u7570\u7684\u4fdd\u6696\u529f\u80fd\uff0c\u4e5f\u6709\u8f03\u9577\u4e45\u7684\u8010\u7528\u5ea6\uff0c\u4f46\u7f3a\u9ede\u662f\u4e0d\u6297\u6c34\u3001\u5bb9\u6613\u53d7\u6f6e\uff0c\u4e00\u65e6\u6fd5\u6389\u5c31\u6c92\u6709\u4fdd\u6696\u6548\u679c\uff0c\u6240\u4ee5\u9632\u6c34\u4e00\u5b9a\u8981\u505a\u597d\u3002\\n:::\\n\\n\x3c!--\\n:::note\\nLorem ipsum dolor sit amet, consectetur adipiscing elit. Pellentesque elementum dignissim ultricies. Fusce rhoncus ipsum tempor eros aliquam consequat. Lorem ipsum dolor sit amet\\n:::\\n\\n:::tip\\nLorem ipsum dolor sit amet, consectetur adipiscing elit. Pellentesque elementum dignissim ultricies. Fusce rhoncus ipsum tempor eros aliquam consequat. Lorem ipsum dolor sit amet\\n:::\\n\\n:::important\\nLorem ipsum dolor sit amet, consectetur adipiscing elit. Pellentesque elementum dignissim ultricies. Fusce rhoncus ipsum tempor eros aliquam consequat. Lorem ipsum dolor sit amet\\n:::\\n\\n:::warning\\nLorem ipsum dolor sit amet, consectetur adipiscing elit. Pellentesque elementum dignissim ultricies. Fusce rhoncus ipsum tempor eros aliquam consequat. Lorem ipsum dolor sit amet\\n:::\\n\\n:::information\\nLorem ipsum dolor sit amet, consectetur adipiscing elit. Pellentesque elementum dignissim ultricies. Fusce rhoncus ipsum tempor eros aliquam consequat. Lorem ipsum dolor sit amet\\n:::\\n\\n:::example\\nLorem ipsum dolor sit amet, consectetur adipiscing elit. Pellentesque elementum dignissim ultricies. Fusce rhoncus ipsum tempor eros aliquam consequat. Lorem ipsum dolor sit amet\\n::: --\x3e"},{"id":"24012","metadata":{"permalink":"/doc_camp/blog/24012","source":"@site/blog/2021-08-01.mdx","title":"\u3010\u7522\u54c1\u3011Smartwool\u651c\u624b\u7642\u7652\u7cfb\u63d2\u756b\u5bb6\uff01\u7e6a\u51fa\u79cb\u51ac\u65b0\u54c1\u6236\u5916\u63a2\u7d22\u7cbe\u795e","description":"\u5f8c\u75ab\u60c5\u6642\u4ee3\u6539\u8b8a\u4e86\u73fe\u4ee3\u4eba\u7684\u751f\u6d3b\u65b9\u5f0f\uff0c\u63a2\u7d22\u6236\u5916\u66f4\u878d\u5165\u4eba\u5011\u7684\u65e5\u5e38\uff0c\u4e0d\u50c5\u50c5\u662f\u5728\u5fd9\u788c\u751f\u6d3b\u591a\u4e00\u4e9b\u7684\u6642\u9593\u7559\u767d\uff0c\u4e5f\u662f\u6162\u6d3b\u7642\u7652\u3001\u81ea\u6211\u5c0d\u8a71\u7684\u597d\u6a5f\u6703\u3002\u7f8e\u570b\u667a\u6167\u7f8a\u6bdb\u9818\u5c0e\u54c1\u724c Smartwool \u4eca\u5e74\u51ac\u5b63\u518d\u6b21\u8207\u77e5\u540d\u63d2\u756b\u5bb6 manualforall \u5408\u4f5c\uff0c\u4ee5\u300c\u6236\u5916\u63a2\u96aa\u300d\u70ba\u7406\u5ff5\uff0c\u7528\u5857\u9d09\u7684\u65b9\u5f0f\u63cf\u7e6a\u51fa\u7a7f\u68ad\u5728\u96ea\u5b63\u3001\u661f\u7403\u5192\u96aa\uff0c\u8b93\u57ce\u5e02\u7537\u5973\u4e00\u8d77\u5f9c\u5f89\u5728\u6236\u5916\u63a2\u96aa\u7684\u5947\u5e7b\u65c5\u7a0b\uff01","date":"2021-08-01T00:00:00.000Z","formattedDate":"August 1, 2021","tags":[{"label":"\u7522\u54c1\u6d3b\u52d5","permalink":"/doc_camp/blog/tags/\u7522\u54c1\u6d3b\u52d5"}],"readingTime":5.065,"hasTruncateMarker":true,"authors":[{"name":"Colo","title":"\u8d85\u7d1a\u5beb\u624b","url":"https://www.facebook.com/Seitnm","imageURL":"https://scontent.fkhh2-2.fna.fbcdn.net/v/t39.30808-6/290351564_5183532028426266_378736563349215874_n.jpg?_nc_cat=104&ccb=1-7&_nc_sid=09cbfe&_nc_ohc=tvF3n5HCZ90AX_2Jp3P&_nc_ht=scontent.fkhh2-2.fna&oh=00_AfD3dV89Rknt8pPwP6KlElxtTnudigpsa_-SH4ik_hgnBA&oe=63712393","key":"colo"}],"frontMatter":{"slug":"24012","title":"\u3010\u7522\u54c1\u3011Smartwool\u651c\u624b\u7642\u7652\u7cfb\u63d2\u756b\u5bb6\uff01\u7e6a\u51fa\u79cb\u51ac\u65b0\u54c1\u6236\u5916\u63a2\u7d22\u7cbe\u795e","authors":"colo","tags":["\u7522\u54c1\u6d3b\u52d5"]},"prevItem":{"title":"\u3010\u5c71\u77e5\u8b58\u3011\u4e09\u500b\u4fdd\u6696\u91cd\u9ede\uff01\u5e36\u4f60\u627e\u5230\u6700\u9069\u5408\u7684\u7fbd\u7d68","permalink":"/doc_camp/blog/24011"}},"content":"\u5f8c\u75ab\u60c5\u6642\u4ee3\u6539\u8b8a\u4e86\u73fe\u4ee3\u4eba\u7684\u751f\u6d3b\u65b9\u5f0f\uff0c\u63a2\u7d22\u6236\u5916\u66f4\u878d\u5165\u4eba\u5011\u7684\u65e5\u5e38\uff0c\u4e0d\u50c5\u50c5\u662f\u5728\u5fd9\u788c\u751f\u6d3b\u591a\u4e00\u4e9b\u7684\u6642\u9593\u7559\u767d\uff0c\u4e5f\u662f\u6162\u6d3b\u7642\u7652\u3001\u81ea\u6211\u5c0d\u8a71\u7684\u597d\u6a5f\u6703\u3002\u7f8e\u570b\u667a\u6167\u7f8a\u6bdb\u9818\u5c0e\u54c1\u724c Smartwool \u4eca\u5e74\u51ac\u5b63\u518d\u6b21\u8207\u77e5\u540d\u63d2\u756b\u5bb6 manual_for_all \u5408\u4f5c\uff0c\u4ee5\u300c\u6236\u5916\u63a2\u96aa\u300d\u70ba\u7406\u5ff5\uff0c\u7528\u5857\u9d09\u7684\u65b9\u5f0f\u63cf\u7e6a\u51fa\u7a7f\u68ad\u5728\u96ea\u5b63\u3001\u661f\u7403\u5192\u96aa\uff0c\u8b93\u57ce\u5e02\u7537\u5973\u4e00\u8d77\u5f9c\u5f89\u5728\u6236\u5916\u63a2\u96aa\u7684\u5947\u5e7b\u65c5\u7a0b\uff01\\n\\n\x3c!--truncate--\x3e\\n\\n![image](https://cdntwrunning.biji.co/800_ec3e3b3d64720e7624f1b8942fb0ecc7.jpg)\\n\u651c\u624b\u63d2\u756b\u5bb6 manual_for_all \u4ee5\u7e7d\u7d1b\u7684\u8272\u5f69\u8207\u5716\u6848\uff0c\u50b3\u9054\u4eba\u5011\u5728\u300c\u6236\u5916\u63a2\u7d22\u300d\u7684\u7f8e\u597d\u9ad4\u9a57\\n\\n### Smartwool \u8207\u63d2\u756b\u5bb6\u5408\u4f5c\u63a8\u51fa\u806f\u540d\u670d\u98fe \u7642\u7652\u7cfb\u5857\u9d09\u5c55\u73fe\u52c7\u65bc\u63a2\u96aa\u7cbe\u795e\\n\\n\u7e7c 2022 \u4e0a\u534a\u5e74\u63a8\u51fa\u7b2c\u4e00\u6ce2\u806f\u540d\u7cfb\u5217\u5f8c\uff0cSmartwool \u518d\u5ea6\u806f\u624b\u53f0\u7063\u63d2\u756b\u5bb6 manual_for_all \u65bc\u79cb\u51ac\u91cb\u51fa\u5168\u65b0\u529b\u4f5c\u3002\u6b64\u6b21\u79cb\u51ac\u806f\u540d\u9f13\u52f5\u5927\u5bb6\u52c7\u65bc\u63a2\u96aa\uff0c\u5973\u6b3e\u4ee5\u300e\u592a\u7a7a\u63a2\u96aa\u300f\u70ba\u4e3b\u984c\uff0c\u904b\u7528\u7ae5\u8da3\u5857\u9d09\u7e6a\u51fa\u8df3\u812b\u6846\u67b6\u7684\u62bd\u8c61\u6982\u5ff5\uff0c\u4eba\u985e\u53ef\u4e0a\u5916\u592a\u7a7a\u3001\u8070\u660e\u7f8a\u4e5f\u80fd\u548c\u5927\u91ce\u72fc\u6210\u70ba\u597d\u53cb\uff0c\u5f37\u8abf\u6c88\u6d78\u5728\u6236\u5916\u6d3b\u52d5\u6216\u662f\u4efb\u4f55\u611b\u597d\u6642\uff0c\u7e3d\u6709\u4e9b\u51fa\u4e4e\u610f\u6599\u7684\u7f8e\u597d\u4e8b\u7269\u767c\u751f\uff0c\u7336\u5982\u63a2\u7d22\u672a\u77e5\u7684\u661f\u7403\uff0c\u4ee4\u4eba\u7121\u6bd4\u56ae\u5f80\u3002\u800c\u7537\u6b3e\u300e\u96ea\u5b63\u63a2\u96aa\u300f\u4ee5\u67d4\u548c\u7c21\u7d04\u7684\u914d\u8272\u53ca\u53ef\u611b\u7642\u7652\u7684\u6f2b\u756b\u98a8\u683c\uff0c\u63cf\u7e6a\u51fa\u65b0\u624b\u7df4\u7fd2\u6ed1\u96ea\u7684\u5fc3\u5883\uff0c\u50b3\u9054\u81ea\u6211\u767c\u6398\u904e\u7a0b\u53ca\u63a2\u7d22\u65b0\u9818\u57df\u7684\u7f8e\u597d\u6642\u5149\uff0c\u9f13\u52f5\u4eba\u5011\u7a7f\u4e0a Smartwool \u4eab\u53d7\u65e5\u5e38\u751f\u6d3b\u4e2d\u4e0d\u540c\u9a5a\u559c\uff0c\u52c7\u6562\u63a2\u7d22\u6236\u5916\u3002\\n\\n![image](https://cdntwrunning.biji.co/800_310d243fd6ed4f4ef91fec732dfe7208.png)\\n\u63d2\u756b\u5bb6 manual_for_all\\n\\n### \u6a5f\u80fd\u8207\u4fcf\u76ae\u98a8\u683c\u517c\u5177 \u5f9e\u57ce\u5e02\u5230\u6236\u5916\u90fd\u80fd\u6709\u7684 Outdoor Style\\n\\nSmartwool \u79cb\u51ac\u65b0\u54c1\u63a8\u51fa\u591a\u6b3e\u51ac\u5b63\u7cfb\u5217\u7f8a\u6bdb\u670d\u98fe\u3001\u896a\u5b50\u3001\u5e3d\u5b50\u548c T-shirt\uff0c\u5e03\u6599\u7d50\u5408\u7f8e\u9e97\u8afe\u7f8a\u6bdb\uff0c\u5177\u6709\u6291\u83cc\u6297\u81ed\u3001\u5feb\u4e7e\u8207\u6eab\u5ea6\u63a7\u5236\u7684\u7279\u6027\uff0c\u53ef\u61c9\u4ed8\u6236\u5916\u591a\u8b8a\u6c23\u5019\uff1b\u670d\u98fe\u5229\u7528\u5e73\u8eca\u7e2b\u7dda\u8a2d\u8a08\uff0c\u6e1b\u4f4e\u8863\u7269\u6469\u64e6\u611f\uff0c\u70ba\u63a2\u96aa\u8005\u63d0\u4f9b\u6700\u4f73\u9632\u8b77\u8207\u8212\u9069\u5ea6\uff1b\u800c\u8a2d\u8a08\u4e0a\u4ee5\u5857\u9d09\u5546\u54c1\u5448\u73fe\u4fcf\u76ae\u53ef\u611b\u7684\u6236\u5916\u4f11\u9592\u98a8\u683c\uff0c\u4e0d\u8ad6\u662f\u5065\u884c\u9732\u71df\u6216\u6bcf\u500b\u65e5\u5e38\uff0c\u90fd\u80fd\u5920\u4eab\u53d7\u8212\u9069\u81ea\u5728\u7684\u7f8e\u597d\u6642\u5149\u3002\\n\\n![image](https://cdntwrunning.biji.co/800_211d63cbe404c8bd1fd9ad7bb71e3580.jpg)\\nSmartwool \u79cb\u51ac\u65b0\u54c1\u5df2\u5728\u5404\u7d93\u92b7\u53ca\u76f4\u71df\u9580\u5e02\u8ca9\u552e\uff0c\u4e0d\u8ad6\u4e0a\u5c71\u4e0b\u6d77\u3001\u90ca\u5c71\u5065\u884c\u3001\u5468\u672b\u9732\u71df\u90fd\u80fd\u6253\u9020\u51fa\u7368\u4e00\u7121\u4e8c\u7684\u6236\u5916\u63a2\u96aa\u98a8\u683c\\n\\n### \u7528\u63d2\u756b\u63cf\u7e6a\u51fa\u63a2\u7d22\u4e2d\u7684\u4f60 GQ \u91ce\u71df\u6eff\u984d\u53ef\u4eab\u63d2\u756b\u5bb6 manual_for_all \u4f3c\u984f\u7e6a\u670d\u52d9\\n\\n\u7a7f\u68ad\u5728\u57ce\u5e02\u8207\u6236\u5916\u9593\uff0c\u7a7f\u4e0a Smartwool \u7f8a\u6bdb\u670d\u98fe\u642d\u914d\u7387\u6027\u5927\u5730\u8272\u8932\u88dd\u6216\u662f\u7c21\u7d04\u6c23\u8cea\u9577\u88d9\uff0c\u4e00\u8eab\u8f15\u9b06\u81ea\u5728\u7684\u670d\u88dd\u98a8\u683c\uff0c\u4e0d\u8ad6\u5728\u57ce\u5e02\u6216\u6236\u5916\u90fd\u80fd\u6253\u9020\u51fa\u7368\u4e00\u7121\u4e8c\u7684\u6236\u5916\u63a2\u96aa\u98a8\u683c\u3002\\n\\nSmartwool \u5c07\u65bc 11/5(\u516d) \u53c3\u8207\u300cGQ \u57ce\u5e02\u91ce\u71df\u5609\u5e74\u83ef\u300d\u6d3b\u52d5\uff0c\u7576\u5929\u4f86\u5e97\u5373\u53ef\u7372\u5f97 manual_for_all \u8212\u9069\u5c0f\u5c4b\u9650\u91cf\u63d2\u756b\u5c0f\u5361\u4e00\u5f35\uff1b\u8cfc\u8cb7\u4efb\u5169\u96d9\u65b0\u54c1\u7f8a\u6bdb\u896a\u5373\u8d08\u7ae5\u8da3\u98db\u76e4(\u6578\u91cf\u6709\u9650\uff0c\u9001\u5b8c\u70ba\u6b62)\uff1b\u53e6\u5916\u9084\u6709 manual_for_all \u4f3c\u984f\u7e6a\u6d3b\u52d5\uff1a\u7576\u65e5\u6eff$2,500 \u5373\u53ef\u7372\u5f97\u4f3c\u984f\u7e6a\u4e59\u6b21(\u5e02\u50f9$500)\u3002Smartwool \u8207\u63d2\u756b\u5bb6 manual_for_all \u806f\u540d\u7cfb\u5217\u5546\u54c1\u5df2\u5728\u5404\u7d93\u92b7\u53ca\u76f4\u71df\u9580\u5e02\u8ca9\u552e\uff0c\u9080\u8acb\u5927\u5bb6\u4e00\u8d77\u7a7f\u4e0a Smartwool\u3001\u80cc\u4e0a\u884c\u56ca\uff0c\u8e0f\u4e0a\u51ac\u5929\u7684\u6236\u5916\u63a2\u96aa\u65c5\u9014\uff01\\n\\n### About Smartwool\uff1a\u98a8\u9761\u5168\u7403\u8fd1 30 \u5e74\u7684\u8070\u660e\u7f8a\u6bdb\\n\\n\u4f86\u81ea\u7f8e\u570b\u79d1\u7f85\u62c9\u591a\u5dde\uff0c\u7531\u4e00\u7fa4\u71b1\u611b\u6ed1\u96ea\u7684\u597d\u624b\u6240\u5275\u7acb\uff0c\u98a8\u9761\u5168\u7403\u8fd1\u4e09\u5341\u5e74\u3002\u9078\u7528 ZQ \u8a8d\u8b49\u7684\u9802\u7d1a\u7d10\u897f\u862d\u7f8e\u9e97\u8afe\u7f8a\u6bdb\uff0c\u5275\u9020\u9ad8\u6a5f\u80fd\u4e14\u89aa\u819a\u7d30\u7dfb\u7684\u8863\u896a\u53ca\u914d\u4ef6\u5546\u54c1\u3002\u512a\u7570\u7684\u6291\u83cc\u6297\u81ed\u3001\u5438\u6fd5\u6392\u6c57\uff0c\u5728\u708e\u71b1\u7684\u590f\u5929\u4e5f\u80fd\u4e7e\u723d\u8212\u9069\uff1b\u7d55\u4f73\u7684\u6eab\u63a7\u6a5f\u80fd\uff0c\u5e6b\u52a9\u8abf\u7bc0\u4eba\u9ad4\u7dad\u6301\u5728\u6700\u8212\u9069\u7684\u6eab\u5ea6\u3002\u670d\u98fe\u8207\u896a\u6b3e\u642d\u8f09\u53ef\u611b\u5716\u9a30\uff0c\u7d66\u60a8\u8996\u89ba\u8207\u89f8\u89ba\u7684\u96d9\u91cd\u4eab\u53d7\u3002\\n\\n- [\u5b98\u65b9\u7db2\u7ad9](https://www.starlike.com.tw/blog/brands/smartwool/)\\n- [\u5b98\u65b9\u7c89\u7d72\u5718](https://www.facebook.com/SmartwoolTaiwan)\\n- [\u5b98\u65b9 IG](https://www.instagram.com/smartwool_taiwan/)"}]}')}}]);